void printar(char final[25][32][5]) {
  for(int i = 0; i < 25; i++) {
    for(int j = 0; j < 32; j++) {
      printf("%s", final[i][j]);
    }

    printf("\n");
  }
}

void rotateRight(char arr[25][32][5], int linha, int tamanho) {
    char last[5];
    strcpy(last, arr[linha][tamanho-1]);

    for (int i = tamanho-1; i > 0; i--) {
      strcpy(arr[linha][i], arr[linha][i-1]);
    }

    strcpy(arr[linha][0], last);
}

